import { useCallback, useEffect, useState } from "react"
import { RequestByEmployeeParams, Transaction } from "../utils/types"
import { TransactionsByEmployeeResult } from "./types"
import { useCustomFetch } from "./useCustomFetch"
import { Employee } from "../utils/types"

export function useTransactionsByEmployee(): TransactionsByEmployeeResult {
  const { fetchWithoutCache, fetchWithCache, loading } = useCustomFetch()
  const [transactionsByEmployee, setTransactionsByEmployee] = useState<Transaction[] | null>(null)
  const [allEmployeeTransactions, setAllEmployeeTransactions] = useState<[Transaction[]] | null>(null)

  const fetchAllEmployeeTransactions = useEffect(() => {
    const getData = async () => {
      const employeeData = await fetchWithoutCache<Employee[]>("employees")
      if (employeeData) {
        var employeeTransactions: [Transaction[]] | null
        employeeData.forEach(
          async (employee) =>
            await fetchWithCache<Transaction[], RequestByEmployeeParams>("transactionsByEmployee", {
              employeeId: employee.id,
            }).then((transaction) => {
              if (transaction) {
                employeeTransactions
                  ? employeeTransactions.concat(transaction)
                  : (employeeTransactions = [transaction])
              }
            })
        )
      }
    }
    getData()
    setAllEmployeeTransactions(employeeTransactions)
  }, [])

  const fetchById = useCallback(
    (employeeId: string) => {
      const data = allEmployeeTransactions?.filter((transaction) =>
        transaction.every((item) => item.employee.id === employeeId)
      )
      console.log(data)
      // const data = allEmployeeTransactions?.filter((transaction) => console.log(transaction))
      // setTransactionsByEmployee(data)
    },
    [allEmployeeTransactions]
  )

  const invalidateData = useCallback(() => {
    setTransactionsByEmployee(null)
  }, [])

  return { data: transactionsByEmployee, loading, fetchById, invalidateData }
}
