import {
  PaginatedRequestParams,
  PaginatedResponse,
  RequestByEmployeeParams,
  SetTransactionApprovalParams,
  Transaction,
  Employee,
  AppDataParams,
  CacheDataParams,
} from "./types"
import mockData from "../mock-data.json"
import { useContext } from "react"
import { AppContext } from "./context"

const TRANSACTIONS_PER_PAGE = 5

const useUpdateCache = ({ employees, transactions }: CacheDataParams) => {}

const data: AppDataParams = {
  employees: mockData.employees,
  transactions: mockData.transactions,
}

export const getEmployees = (): Employee[] => data.employees

export const getTransactionsPaginated = ({
  page,
}: PaginatedRequestParams): PaginatedResponse<Transaction[]> => {
  if (page === null) {
    throw new Error("Page cannot be null")
  }

  const start = page * TRANSACTIONS_PER_PAGE
  const end = start + TRANSACTIONS_PER_PAGE

  if (start > data.transactions.length) {
    throw new Error(`Invalid page ${page}`)
  }

  const nextPage = end < data.transactions.length ? page + 1 : null

  return {
    nextPage,
    data: data.transactions.slice(start, end),
  }
}

export const getTransactionsByEmployee = ({ employeeId }: RequestByEmployeeParams) => {
  if (!employeeId) {
    throw new Error("Employee id cannot be empty")
  }

  return data.transactions.filter((transaction) => transaction.employee.id === employeeId)
}

export const setTransactionApproval = ({
  transactionId,
  value,
}: SetTransactionApprovalParams): boolean => {
  const Newtransaction = data.transactions.find(
    (currentTransaction) => currentTransaction.id === transactionId
  )
  if (!Newtransaction) {
    throw new Error("Invalid transaction to approve")
  }

  data.transactions.forEach((transaction, i, arr) => {
    if (transaction.id === transactionId) {
      arr[i].approved = value
    }
  })
  return Newtransaction.approved
}
