import { useContext, useCallback, useState } from "react"
import { AppContext } from "src/utils/context"
import { fakeFetch } from "../utils/fetch"
import { useWrappedRequest } from "./useWrappedRequest"

export function useCache() {
  const { cache } = useContext(AppContext)
  const { updateCache, setUpdateCache } = useState<boolean>(false)
  const { wrappedRequest } = useWrappedRequest()

  const getData = useCallback(
    async (endpoint, params) =>
      wrappedRequest(async () => {
        const cacheKey = getCacheKey(endpoint, params)
        const cacheResponse = cache?.current.get(cacheKey)

        if (!updateCache && cacheResponse) {
          const data = JSON.parse(cacheResponse)
          return data
        }

        const result = await fakeFetch(endpoint, params)
        cache?.current.set(cacheKey, JSON.stringify(result))
        setUpdateCache(!updateCache)
        return result
      }),
    [cache, setUpdateCache, updateCache, wrappedRequest]
  )

  return { getData }
}

function getCacheKey(endpoint, params) {
  return `${endpoint}${params ? `@${JSON.stringify(params)}` : ""}`
}
